var glslify = require('glslify-promise');

var Context   = require('pex-glu').Context;
var Material  = require('pex-glu').Material;
var Color     = require('pex-color').Color;
var Vec2      = require('pex-geom').Vec2;
var merge     = require('merge');
var Program   = require('pex-glu-program-promise');

function HaloRingMaterial(uniforms) {
  this.gl = Context.currentContext;
  var program = new Program(glslify(__dirname + '/HaloRing.glsl'));
  var defaults = {
    lineTexture: null
  };
  uniforms = merge(defaults, uniforms);
  Material.call(this, program, uniforms);
}

HaloRingMaterial.prototype = Object.create(Material.prototype);

module.exports = HaloRingMaterial;